            List<Author> yazarlar = new List<Author>()
            {
                new Author(){ Name = "İlber Ortaylı", Description = "1947 senesinde Avusturya'da doğdu. 2 yașındayken ailesiyle birlikte Türkiye'ye göç etti. İlk ve orta öğrenimini İstanbul Avusturya Lisesi'nde tamamladı. 1965 yılında Ankara Atatürk Lisesi'nden mezun oldu. Akademik kariyeri 1969 yılında Ankara Üniversitesi Siyasal Bilgiler Fakültesi'ni ve Ankara Üniversitesi Dil, Tarih ve Coğrafya Fakültesi'nin tarih bölümünü bitirdi. Viyana Üniversitesi Slavistik ve Orientalistik Bölümü'nde öğrenim gördü. Yüksek lisans çalışmasını Chicago Üniversitesi'nde Prof. Dr. Halil İnalcık ile yaptı. Ankara Siyasal Bilgiler Fakültesi'nde Tanzimat Sonrası Mahallî İdareler adlı tezi ile 1974 yılında doktor, Osmanlı İmparatorluğu'nda Alman Nüfûzu adlı çalışmasıyla 1979'da doçent oldu. 1982 yılında devletin akademik politikalarına tepki olarak görevinden istifa etti. Bu dönemde Viyana, Berlin, Paris, Princeton, Moskova, Roma, Münih, Strazburg, Yanya, Sofya, Kiel, Cambridge, Oxford ve Tunus üniversitelerinde misafir öğretim üyeliği yaptı, buralarda seminerler ve konferanslar verdi. 1989'da Türkiye'ye dönerek profesör oldu ve 1989-2002 yılları arasında Ankara Üniversitesi Siyasal Bilgiler Fakültesi'nde İdare Tarihi Bilim Dalı Başkanı olarak görev yaptı. Yerli ve yabancı bilimsel dergilerde 16. ile 19. yüzyıllar arası Osmanlı tarihi ve Rus tarihi ile ilgili makaleleri yayınlandı. 2002 yılında Galatasaray Üniversitesi'ne, iki yıl sonra ise Bilkent Üniversitesi'ne konuk öğretim üyesi olarak geçti. Şu anda Galatasaray Üniversitesi Hukuk Fakültesi ve Bilkent Üniversitesi Hukuk Fakültesinde Türk Hukuk Tarihi derslerini vermektedir. Galatasaray Üniversitesi Senato üyesidir. Ayrıca İlke Eğitim ve Sağlık Vakfı Kapadokya Meslek Yüksekokulu Mütevelli Heyeti üyesidir. ..." , Image = "ilber-ortayli.jpg"},
                 new Author(){ Name = "A. M. Celal Şengör", Description = "24 Mart 1955 tarihinde İstanbul'da doğdu. 1973 tarihinde Robert Koleji'ni bitirdikten sonra 1978'de ise State University of New York at Albany'den Jeolog olarak mezun oldu ve aynı üniversiteden 1979'da yüksek lisansını bitirdi. 1981'de İstanbul Teknik Üniversitesi Maden Fakültesi, Genel Jeoloji kürsüsünde asistan olarak görev yapmaya başladı. 1982'de de State University of New York at Albany'den doktora aldı. 1984 yılında Londra Jeoloji Cemiyeti'nin Başkanlık Ödülü'nü, 1986'da TÜBİTAK Bilim Ödülü'nü kazandı. Aynı yıl İstanbul Teknik Üniversitesi Maden Fakültesi Genel Jeoloji Anabilim Dalında doçent oldu. 1988'de Neuchâtel Üniversitesi Fen Fakültesi'nden şeref bilim doktoru unvanına sahip oldu. 1990 yılında Academia Europaea'ya kabul edilden Celal Şengör, aynı yıl Avusturya Jeoloji Servisi muhabir üyesi, 1991 yılında ise Avusturya Jeoloji Derneği şeref üyesi oldu. Yine 1991 yılında Kültür Bakanlığı'nın Bilgi Çağı Ödülünü kazandı. 1992 yılında İstanbul Teknik Üniversitesi Maden Fakültesi Genel Jeoloji Anabilim Dalı'nda profesörlüğe yükseldi. Celal Şengör, ileri seviyede İngilizce, Fransızca ve Almanca bildiğini ifade ederken; Felemenkçe, İtalyanca, Portekizce, İspanyolca ve Osmanlı Türkçesini okuyabildiğini de söylemiştir.", Image = "celal-sengor.jpg" },
                  new Author(){ Name = "Stefan Zweig", Description = "STEFAN ZWEIG, 1881’de Viyana’da doğdu. Avusturya, Fransa ve Almanya’da öğrenim gördü. Savaş karşıtı kişiliğiyle dikkat çekti. 1919-1934 yılları arasında Salzburg’da yaşadı, Nazilerin baskısı yüzünden Salzburg’u terk etmek zorunda kaldı. 1938’de İngiltere’ye, 1939’da New York’a gitti, birkaç ay sonra da Brezilya’ya yerleşti. Önceleri Verlaine, Baudelaire ve Verhaeren çevirileriyle tanındı, ilk şiirlerini ise 1901’de yayımladı. Çok sayıda deneme, öykü, uzun öykünün yanı sıra büyük bir ustalıkla kaleme aldığı yaşamöyküleriyle de ünlüdür. Psikolojiye ve Freud’un öğretisine duyduğu yoğun ilgi, Zweig’ın derin karakter incelemelerinde ifade bulur. Özellikle tarihsel karakterler üzerine yazdığı yorumlar ve yaşamöyküleri, psikolojik çözümlemeler bakımından son derece zengindir. Zweig, Avrupa’nın içine düştüğü siyasi duruma dayanamayarak 1942’de Brezilya’da karısıyla birlikte intihar etti.", Image = "stefan-zweig.jpg"},
                  new Author(){ Name = "Sabahattin Ali", Description = "25 Şubat 1907’de Gümülcine’de doğdu, 2 Nisan 1948’de Kırklareli’nde öldü.1948’de bir yazısı yüzünden tutuklandı, üç ay kadar hapis yattı. Sürekli izlendiği için yurtdışına kaçmak istedi, ancak Kırklareli dolaylarında bir kaçakçı tarafından öldürüldüğü iddia edildi. Şiirler, hikâyeler, romanlar yazdı, çeviriler yaptı.", Image = "sabahattin-ali.jpg"},
                  new Author(){ Name = "George Orwell", Description = "GEORGE ORWELL, 1903’te Hindistan’ın Bengal eyaletinin Montihari kentinde doğdu. Ailesiyle birlikte İngiltere’ye döndükten sonra, öğrenimini Eton College’de tamamladı. Gerçek adı Eric Arthur Blair olan Orwell, 1922-1927 yılları arasında Hindistan İmparatorluk Polisi olarak görev yaptı. Ancak imparatorluk yönetiminin içyüzünü görünce istifa etti. 1950’de yayımladığı Shooting an Elephant (Bir Fili Vurmak) adlı kitabı, sömürge memurlarının davranışlarını eleştiren makalelerin derlemesidir. İkinci Dünya Savaşı’nın sonlarına doğru yazdığı Hayvan Çiftliği, Stalin rejimine karşı sert bir taşlamadır. Orwell’in en çok tanınan yapıtlarından Bin Dokuz Yüz Seksen Dört, bilimkurgu türünün klasik örneklerinden biri olmanın yanı sıra, modern dünyayı protesto eden bir romandır. Burma Günleri ise, Orwell’in Burma’daki (bugünkü Myanmar) İngiliz sömürgeciliğini dile getirdiği ilk kitabıdır. Orwell 1950’de Londra’da öldü.", Image = "george-orwell.jpg"},
                  new Author(){ Name = "Zülfü Livaneli", Description = "Romanları 30 dilde yayımlanan Zülfü Livaneli, 1946 yılında doğdu. 1972 yılında fikirlerinden dolayı askeri cezaevinde yattı, 11 yıl sürgünde yaşadı. Livaneli, 1999 yılında San Remo’da En İyi Besteci ödülüne layık görüldü. Müzik eserleri Londra, Moskova, Berlin, Atina, İzmir Senfoni orkestraları tarafından icra edildi ve Zubin Mehta, Simeon Kogan gibi şeflerce yönetildi.Türkiye dışında Çin Halk Cumhuriyeti, İspanya, Kore ve Almanya’da da çok satanlar arasına giren romanlarıyla, Balkan Edebiyat Ödülü’ne, ABD’de Barnes and Noble Büyük Yazar Ödülü’ne, İtalya ve Fransa’da Yılın Kitabı Ödülü’ne, Türkiye’de ise Yunus Nadi Ödülü’ne ve Orhan Kemal Roman Ödülü’ne layık görüldü. Livaneli, dünya kültür ve barışına yaptığı katkılardan ötürü 1996 yılında Paris’te UNESCO tarafından Büyükelçilikle onurlandırıldı ve Genel Direktör danışmanlığına atandı.2002-2006 yılları arasında TBMM’de ve Avrupa Konseyi’nde milletvekilliği görevinde bulundu.", Image = "zulfu-livaneli.jpg"},
                  new Author(){ Name = "Platon(Eflatun)", Description = "(İÖ 427-348/347) Soylu bir ailenin oğluydu. Sokrates’in ölümüne kadar onun derslerini izledi. Ardından Mısır ve Güney İtalya’yı da kapsayan uzun bir yolculuğa çıktı. İÖ 387’de Atina’ya dönerek Akademia adlı okulu kurdu. Kendisinin olduğu kabul edilen 28 diyalogda, hocası Sokrates’in konuşmalarını bir araya getirir. Bunların en önemlilerinden bazıları şunlardır: Phaidon, Gorgias, Menon, Politikos.", Image = "platon.jpg"},

            };

            foreach (var yazar in yazarlar)
            {
                context.Authors.Add(yazar);
